package main

import "fmt"

func chalkReplacer(chalk []int, k int) int {
	sum := 0
	for _, v := range chalk {
		sum += v
	}
	k = k % sum
	i := 0
	for k > 0 && k >= chalk[i] {
		k -= chalk[i]
		i++
	}
	return i
}

